mapper.select:
SELECT tag_id, tag_name, resource_id, resource_type, user_id
FROM tb_tag_resource_map
WHERE resource_id = #{resourceId}

SELECT tag_id, tag_name, resource_id, resource_type, user_id
FROM tb_tag_resource_map
WHERE user_id = #{userId}

SELECT tag_id, tag_name, resource_id, resource_type, user_id
FROM tb_tag_resource_map
WHERE tag_name = #{tagName}

SELECT resource_id
FROM tb_tag_resource_map
WHERE tag_name = #{tagName}

mapper.delete:
DELETE
FROM tb_tag_resource_map
WHERE tag_name = #{tagName}
  AND resource_id = #{resourceId}
